│   │   │           ├── config/                # 配置中心（Spring Boot标准）
│   │   │           │   ├── GitHubConfig.java   # OAuth认证配置 
│   │   │           │   └── TemplateConfig.java  # 模板引擎配置 
│   │   │           ├── core/
